package statik;

public class Math1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double x=Math.add(10.5, 12.4);
		System.out.println("addition="+x);
		double y=Math.sub(25.5, 12.4);
		System.out.println("Subraction="+y);
		double z=Math.mul(10.5, 12.4);
		System.out.println("Multiplication="+z);
		double s=Math.div(34.5, 12.4);
		System.out.println("Divition="+s);
		

	}

}
